mapper.select:
   select user.*,user_token.token from user left outer join user_token on user.id=user_token.user_id where user.type != 'admin'   

		and user.account=#{account}

   order by user.id desc  

   		limit #{offset},#{perPageCount}

mapper.update:
update user set nick=#{nick}
telephone=#{telephone},email=#{email},gender=#{gender},birthday=#{birthday},real_name=#{real_name}
where id=#{id}
